/**
 * Module dependencies.
 */

require('mocha-generators')
  .install();

var Nightmare = require('nightmare');
var should = require('chai')
  .should();
var url = require('url');
var server = require('./server');
var fs = require('fs');
var mkdirp = require('mkdirp');
var path = require('path');
var rimraf = require('rimraf');

/**
 * Temporary directory
 */

var tmp_dir = path.join(__dirname, 'tmp')

/**
 * Get rid of a warning.
 */

process.setMaxListeners(0);

/**
 * Locals.
 */

var base = 'http://localhost:7500/';

describe('Nightmare download manager', function() {
  before(function(done) {
    require('../nightmare-download-manager')(Nightmare);
    server.listen(7500, done);
  });

  describe('downloads', function() {
    var nightmare;

    before(function(done) {
      mkdirp(path.join(tmp_dir, 'subdir'), done);
    })

    after(function(done) {
      rimraf(tmp_dir, done)
    })

    afterEach(function * () {
      yield nightmare.end();
    });

    it('should download a file', function * () {
      var downloadItem, statFail = false;

      nightmare = Nightmare({
        paths: {
          'downloads': tmp_dir
        }
      });

      yield nightmare
        .downloadManager()
        .goto(fixture('downloads'))
        .click('#dl-tiny')
        .waitDownloadsComplete();

      console.dir(nightmare._downloads);

      downloadItem = nightmare._downloads['10b.txt'];
      try {
        fs.statSync(path.join(tmp_dir, '10b.txt'));
      } catch (e) {
        statFail = true;
      }

      downloadItem.should.be.ok;
      downloadItem.filename.should.equal('10b.txt');
      downloadItem.state.should.equal('completed');
      statFail.should.be.false;
    });
  });
});

/**
 * Generate a URL to a specific fixture.
 * @param {String} path
 * @returns {String}
 */

function fixture(path) {
  return url.resolve(base, path);
}
